mapper.update:
UPDATE FULFILLMENT
SET PLAN_NUM = #{planNum}, COMPLETE_NUM = #{completeNum}, TRY_NUM = ${tryNum}, REGION_DEV = #{regionDev}
WHERE POLITICIAN_NUM = #{politicianNum}

mapper.select:
SELECT (POLITICIAN_NUM, PLAN_NUM, COMPLETE_NUM, TRY_NUM, REGION_DEV)
FROM FULFILLMENT

    WHERE ${searchCode} = #{keyword}
