mapper.update:
UPDATE FULFILLMENT
SET PLAN_NUM = #{planNum}, COMPLETE_NUM = #{completeNum}, TRY_NUM = ${tryNum}, REGION_DEV = #{regionDev}
WHERE POLITICIAN_NUM = #{politicianNum}

mapper.select:
SELECT (POLITICIAN_NUM, PLAN_NUM, COMPLETE_NUM, TRY_NUM, REGION_DEV)
FROM FULFILLMENT

    WHERE ${searchCode} = #{keyword}

SELECT ((COMPLETE_NUM + TRY_NUM/10)/PLAN_NUM + REGION_DEV/100)
FROM FULFILLMENT
WHERE POLITICIAN_NUM = #{politicianNum}

mapper.update:
UPDATE FULFILLMENT
SET FULFILLMENT = ((COMPLETE_NUM + TRY_NUM/10)/PLAN_NUM + REGION_DEV/100)
WHERE POLITICIAN_NUM = #{politicianNum}
