mapper.update:
UPDATE FULFILLMENT
SET PLAN_NUM = #{planNum}, COMPLETE_NUM = #{completeNum}, TRY_NUM = ${tryNum}, REGION_DEV = #{regionDev}
WHERE REGION = #{region}

mapper.select:
SELECT REGION, NAME, PLAN_NUM, COMPLETE_NUM, TRY_NUM, REGION_DEV, FULFILLMENT
FROM FULFILLMENT
WHERE REGION = #{region}

SELECT ((COMPLETE_NUM + TRY_NUM/10)/PLAN_NUM + REGION_DEV/100)
FROM FULFILLMENT
WHERE REGION = #{region}

mapper.update:
UPDATE FULFILLMENT
SET FULFILLMENT = ((COMPLETE_NUM + TRY_NUM/10)/PLAN_NUM + REGION_DEV/100) * 100
WHERE REGION = #{region}
