mapper.update:
UPDATE POLITICIAN
SET LIKES = LIKES+1
WHERE REGION = #{region}

UPDATE PLIKES
SET LIKE_STATE = 0
WHERE MEMBER_ID = #{memberId} AND REGION = #{region}

UPDATE POLITICIAN
SET LIKES = LIKES-1
WHERE REGION = #{region}

mapper.insert:
UPDATE POLITICIAN
SET DISLIKE = DISLIKE+1
WHERE REGION = #{region}

mapper.update:
UPDATE POLITICIAN
SET DISLIKE = DISLIKE-1
WHERE REGION = #{region}

mapper.select:
SELECT COUNT(*)
FROM PLIKES
WHERE MEMBER_ID = #{memberId} AND REGION = #{region}

SELECT COUNT(*)
FROM PLIKES
WHERE MEMBER_ID = #{memberId} AND REGION = #{region}

SELECT LIKE_STATE
FROM PLIKES
WHERE MEMBER_ID = #{memberId} AND REGION = #{region}
