mapper.update:
UPDATE POLITICIAN
SET BIRTH = #{birth}, GRADE = #{grade}
WHERE REGION = #{region}

UPDATE POLITICIAN
SET LIKES = LIKES+1
WHERE REGION = #{region}

UPDATE POLITICIAN
SET DISLIKE = DISLIKE+1
WHERE REGION = #{region}

mapper.select:
SELECT ID, REGION, NAME, LIKES, DISLIKE, BIRTH, GRADE, EDUCATION, CAREER, AWARDS, YOUTUBE, BLOG, INSTAGRAM, KAKAO, PARTY
FROM POLITICIAN
WHERE REGION = #{region}
